  // Bind action when tagged button is pressed.
  $('#tagsubmit').click(function(){
    // get the entire comment and parse it to get tagged friends
    var tags = parseTags($('.as-values').val());
    var btn = $(this);
    // Track tag activity
    mixpanel.track("tag");
    _gaq.push(["_trackEvent", "User-Action", "tag"]);
            
    // set the tag button to loading state to indicate processing
    btn.button('loading');
    $.ajax({
      type: "GET",
      url: "tagfriend",
      data: {pid:$('#hiddenproductid').text(), tags:JSON.stringify(tags), comment:$('#tagmessage').val()}
    }).done(function( data ) {
      // reset state of tag button
      btn.button('reset')
